<template>
<html>
  <head>
    <meta name="viewport" content="width=device-width , initial-scale=1.0">
    </head>
    <body>
  <nav>
    <ul>
      <li class="dropdown"> <a href="#"> Type <span> &rsaquo;</span> </a>
      <ul>
        <li @click="teste"> <a href="#"> histogramme  </a> </li>
          <li> <a href="#"> circulaire</a> </li>
        </ul>
       </li>
        <li class="dropdown"> <a href="#"> <span> &rsaquo;</span>  Color  </a>
        <ul>
          <li>
            <div id="app" v-bind:style="{ backgroundColor: color}">
<h1>Color me</h1>
  <div class="color">
<input type="text" v-bind:style="{ color: color}" v-model="color" maxlength="7" placeholder="#000000"/>
  <input type="color" v-model="color"/>
    </div>
</div>

            </li> </ul>
        </li>
          <li class="dropdown"> <a href="#"> <span> &rsaquo;</span> Size </a>
          <ul>
            <li @click="setmax"> <a href="#"> max </a> </li>
             <li @click="setmin"> <a href="#"> min </a> </li>
              <li @click="setnormal"> <a href="#"> normal </a></li>
          </ul>
           </li>
             <li class="dropdown"> <a href="#"> <span> &rsaquo;</span>  Data</a>
             <ul>
               <li> <input @keyup.enter="add" v-model="num" placeholder="enter a number between 1 and 9..">  </li>

                </ul>

             </li>
            <li> <a href="#"></a></li>
            <li> <router-link id="home" to="/">Home</router-link></li>
      </ul>
    </nav>
    <div id="result" v-bind:style="{ backgroundColor: color , width:width , height:height }"> <h1> {{test}} </h1>
<svg  id="chart" :width="chartWidth" :height="chartHeight" @click="handleClick">
  <g
     v-for="(value, i) in chartInput" :key="i"
     :transform="`translate(0, ${i * (barHeight + 10)})`">
    <rect style="fill:red" :height="barHeight" :width="barWidth(value)"></rect>
    <text :y="barHeight / 2" :x="barWidth(value) - 10">{{ value }}</text>
  </g>
</svg>
    </div>
    </body>
   </html>
</template>
<script>
import * as d3 from 'd3'
import _ from 'lodash'
import dataset from './dataset'
const randomData = () => new Array(6).fill('').map(() => 1 + Math.floor(Math.random() * 20))

// @ is an alias to /src

export default {
  data () {
    return {

      dataset,
      curve: 'curveNatural',

      color: '#b0c4de',
      test: '',
      width: '600px',
      height: '600px',
      chartWidth: 500,
      chartHeight: window.innerHeight - 230,
      chartData: [10, 20, 30, 40, 70],
      num: '',
      chartInput: []
    }
  },
  methods: {
    onClick (item) {
      console.log('hello there', item)
      this.curve = this.curve === 'curveStepAfter' ? 'curveStepBefore' : 'curveStepAfter'
      this.dataset = _.shuffle(this.dataset)
    },
    add () {
      this.chartInput.push(this.num)
      this.num = ''
    },
    barWidth (value) {
      return this.chartWidth / this.dataMax * value
    },
    handleClick () {
      this.chartData = randomData()
    },

    teste () {
      this.test = 'hi'
    },
    setmax () {
      this.height = '750px'
      this.width = '800px'
    },
    setmin () {
      this.height = '400px'
      this.width = '400px'
    },
    setnormal () {
      this.height = '600px'
      this.width = '600px'
    }

  },
  computed: {
    barHeight () {
      return this.chartHeight / this.chartData.length - 10
    },
    dataMax () {
      return Math.max(...this.chartData)
    },

    lineGenerator () {
      return d3
        .line()
        .curve(d3[this.curve])
        .x(v => v[0])
        .y(v => v[1])
    },
    d () {
      return this.lineGenerator(this.dataset)
    }
  }

}
</script>
