<script>
import testd from '../components/testd'
import * as d3 from 'd3'
import _ from 'lodash'
import dataset from './dataset'
const randomData = () =>
  new Array(6).fill('').map(() => 1 + Math.floor(Math.random() * 20))

// @ is an alias to /src

export default {
  components: {
    testd
  },
  data () {
    return {
      sidebaropen: true,
      yScale: null,
      xScale: null,
      colorScale: null,
      categorie: '',
      value: '',
      BarCharts: [
        { id: 1, dollars: 100, label: 'football', color: 'red' },
        { id: 2, dollars: 150, label: 'basket', color: 'blue' },
        { id: 3, dollars: 180, label: 'tennis', color: 'green' },
        { id: 4, dollars: 230, label: 'volley', color: 'yellow' }

      ],
      categories: ['football', 'tennis', 'basket', 'hi', 'hello', 'hi'],
      dollars: [100, 200, 200, 230, 20, 270],
      colors: ['red', 'blue', 'green', 'black', 'yellow', 'blue'],
      dataset,
      curve: 'curveNatural',

      color: '#b0c4de',
      test: '',
      width: '600px',
      height: '600px',
      chartWidth: 500,
      chartHeight: window.innerHeight - 230,
      chartData: [10, 20, 30, 40, 70],
      num: '',
      chartInput: []
    }
  },
  created: function () {
    var grid = d3.range(25).map(function () {
      return { x1: 0, y1: 0, x2: 0, y2: 480 }
    })
    console.log('test123')

    this.xScale = d3.scaleLinear().domain([10, 250]).range([0, 722])

    this.yScale = d3
      .scaleLinear()
      .domain([0, this.categories.length])
      .range([0, 480])

    this.colorScale = d3
      .scaleQuantize()
      .domain([0, this.categories.length])
      .range(this.colors)

    this.grid = grid

    // fill bars
  },
  methods: {
    onClick (item) {
      console.log('hello there', item)
      this.curve =
        this.curve === 'curveStepAfter' ? 'curveStepBefore' : 'curveStepAfter'
      this.dataset = _.shuffle(this.dataset)
    },
    add () {
      this.chartInput.push(this.num)
      this.num = ''
    },
    barWidth (value) {
      return (this.chartWidth / this.dataMax) * value
    },
    handleClick () {
      this.chartData = randomData()
    },

    teste () {
      this.test = 'hi'
    },
    setmax () {
      this.height = '750px'
      this.width = '800px'
    },
    setmin () {
      this.height = '400px'
      this.width = '400px'
    },
    setnormal () {
      this.height = '600px'
      this.width = '600px'
    }
  },
  computed: {
    barHeight () {
      return this.chartHeight / this.chartData.length - 10
    },
    dataMax () {
      return Math.max(...this.chartData)
    },

    lineGenerator () {
      return d3
        .line()
        .curve(d3[this.curve])
        .x((v) => v[0])
        .y((v) => v[1])
    },
    d () {
      return this.lineGenerator(this.dataset)
    }
  }
}
</script>
